ExUnit.start()

defmodule Day1 do
  def captcha([]), do: 0
  def captcha([_]), do: 0
  def captcha(list), do: sum_identicals(list, List.last(list), 0)

  def sum_identicals([head | tail], previous, sum) when head == previous do
    # IO.puts("digits #{ascii_to_int(previous)} and #{ascii_to_int(head)} match, add up and recurse")
    sum_identicals(tail, head, sum + ascii_to_int(head))
  end

  def sum_identicals([head | tail], _previous, sum) do
    # IO.puts("digits #{ascii_to_int(previous)} and #{ascii_to_int(head)} no match, just recurse")
    sum_identicals(tail, head, sum)
  end

  def sum_identicals([], _previous, sum) do
    # IO.puts("reached end, return #{sum}")
    sum
  end

  def ascii_to_int(ascii), do: ascii - ?0
end

defmodule Day1Test do
  use ExUnit.Case

  test "bad input" do
    assert Day1.captcha('') == 0
    assert Day1.captcha('0') == 0
  end

  test "examples" do
    assert Day1.captcha('1122') == 3
    assert Day1.captcha('1111') == 4
    assert Day1.captcha('1234') == 0
    assert Day1.captcha('91212129') == 9
  end

end

defmodule Day1_1 do
  def captcha(''), do: 0
  def captcha([_]), do: 0

  def captcha(list) do
    length = list |> length |> div(2)
    sum_it(list, List.pop_at(list, length), length, 0)
  end

  def sum_it([head | list], {window, _list}, length, sum) when window == head do
    sum = sum + ascii_to_int(head) + ascii_to_int(window)
    sum_it(list, List.pop_at(list, length), length, sum)
  end

  def sum_it([_head | list], {_window, _list}, length, sum) do
    sum_it(list, List.pop_at(list, length), length, sum)
  end

  def sum_it(_list, {nil, _}, _length, sum), do: sum

  def ascii_to_int(ascii), do: ascii - ?0
end

defmodule Day1_1Test do
  use ExUnit.Case

  test "bad input" do
    assert Day1_1.captcha('') == 0
    assert Day1_1.captcha('0') == 0
  end

  test "examples" do
    assert Day1_1.captcha('1212') == 6
    assert Day1_1.captcha('1221') == 0
    assert Day1_1.captcha('123425') == 4
    assert Day1_1.captcha('123123') == 12
    assert Day1_1.captcha('12131415') == 4
  end

end

IO.puts(Day1.captcha(''))
IO.puts(Day1_1.captcha(''))
